INSERT INTO `ol_stage` VALUES ('6', '1', '2', '2', '40221', '鎮關西', '0', '4', '72', '26', '', '0', '', '快活林', '此等奸商居然還開了連鎖店？把你們的營業執照拿出來看看！', '你砸了我們總店那筆帳還沒算呢，今天讓你豎著進來橫著出去！', '', '', '2');
