INSERT INTO `ol_stage` VALUES ('11', '1', '3', '3', '40331', '張團練', '0', '7', '100', '152', '', '0', '', '鴛鴦樓', '又來一波？這私人會所裡養了不少人啊！', '總統套房，King size大床，無敵山景，外籍管家，只要9999一晚。', '', '', '2');
INSERT INTO `ol_stage` VALUES ('12', '1', '3', '4', '40341', '張都監', '0', '7', '300', '628', '斥候鎖甲圖紙', '12201', '', '鴛鴦樓', '總算見到你了。你所轄的區域，很多商販有嚴重的食品衛生問題，伏法吧！', '不乾不淨，吃了沒病。這可是文化人聚集的地方，休得撒野！', '解決了張都監，查封了鴛鴦樓，心情真暢快！兄弟們，咱們去清風寨體驗一下農家樂！', '公費旅遊！哦也！！！', '2');
INSERT INTO `ol_stage` VALUES ('13', '1', '4', '1', '40411', '劉老漢', '0', '8', '129', '114', '', '0', '', '清風寨', '這位老大爺怎麼走著走著自己就倒了？還往我車輪下滾，幸好我及時刹車。', '哎喲哎喲，你撞到老夫了，老夫不能走路了！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('14', '1', '4', '2', '40421', '劉夫人', '0', '8', '129', '114', '', '0', '', '清風寨', '原來是傳說中的碰瓷啊，想做個好人也不容易。我是陽谷縣令，爾等不得放肆！', '少廢話，趕緊賠償醫藥費、精神損失費、青春損失費、服裝道具費，不賠別想走！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('15', '1', '4', '3', '40431', '鎮三山', '1', '9', '129', '122', '', '0', '', '清風寨', '娘子軍來了？好男不和女鬥，讓路！', '哎喲喂，男人打女人啦！快來看哪，縣令欺負百姓啦！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('16', '1', '4', '4', '40441', '劉高', '0', '9', '386', '600', '斥候樸刀圖紙', '14201', '', '清風寨', '劉知寨啊，此地民風也太彪悍了，你這管理工作有難度吧？', '這位老夫子是我七舅姥爺！你一定是酒駕！要麼賠錢！要麼蹲半年！', '這清風寨可真夠亂的，影響兄弟們的好興致。誰會看地圖，下一站是哪裡？', '老大，前面就是江州的地盤了。', '2');
INSERT INTO `ol_stage` VALUES ('17', '1', '5', '1', '40511', '士兵', '0', '10', '158', '222', '', '0', '', '江州城', '哎？這地方怎麼還有公路收費站呢？這又不是高速公路！', '少廢話！交錢走人！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('18', '1', '5', '2', '40521', '黃通判', '0', '10', '158', '222', '', '0', '', '江州城', '又是收費站？我當車匪路霸的時候你們還在穿開襠褲呢！', '好漢不提當年勇！趕緊交錢，要不別想過！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('19', '1', '5', '3', '40531', '監斬官', '0', '11', '158', '234', '', '0', '', '江州城', '怎麼又一個收費站，還有完沒完了？這明明是私設網站亂收費！', '這幾個收費站都是當今太師蔡京的兒子蔡九爺設的，正宗富貴二代，怕了吧？', '', '', '2');
INSERT INTO `ol_stage` VALUES ('20', '1', '5', '4', '40541', '蔡九', '0', '11', '472', '771', '都頭長靴圖紙', '13202', '', '江州城', '蔡九，你不光私設收費站，還隨意提高加油站的油價！今天我就替你爹管教管教你！', '既然知道我爸爸是誰，那就趕緊掏錢。花錢買平安，划算。', '蔡京的兒子又怎麼樣，三兩下就搞定了。兄弟們，咱們去前面村莊買點豬肉，犒賞三軍！', '跟著大哥混，有肉吃！', '2');
INSERT INTO `ol_stage` VALUES ('21', '1', '6', '1', '40611', '李應', '0', '12', '187', '801', '', '0', '', '祝家莊', '你這豬肉是翻著倍漲價的吧？這不搶錢麼？', '愛買不買，豬飼料先漲價，我不漲價就得虧本。', '', '', '2');
INSERT INTO `ol_stage` VALUES ('22', '1', '6', '2', '40621', '扈三娘', '1', '13', '187', '843', '', '0', '', '祝家莊', '你這裡的豬飼料怎麼這麼貴？現在豬肉價格一漲，老百姓都吃不起肉了！', '我也沒辦法，飼料原材料漲價了，我不跟著漲就沒法開店了！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('23', '1', '6', '3', '40631', '祝家三虎', '0', '13', '187', '843', '', '0', '', '祝家莊', '你為什麼要哄抬飼料原材料的價格？你知不知道這直接導致了豬肉價格上漲？', '不關我事啊，祝家莊的祝老莊主是這些原材料的唯一指定總經銷商，我們都是按他規定的價格進貨的。', '', '', '2');
INSERT INTO `ol_stage` VALUES ('24', '1', '6', '4', '40641', '祝莊主', '0', '14', '559', '1362', '都頭髮帶圖紙', '11202', '', '祝家莊', '你身為飼料原材料的總經銷商，隨意調整價格，擾亂物價水準，你當我這縣令是擺設？', '我是唯一指定總經銷商，解釋權歸我所有。', '杯具啊，連豬肉漲價也要我出手，還是以前當山大王時逍遙自在，要是有個壓寨夫人該多好！', '老大，大名府最近在舉辦鵲橋相親會，憑你的人品武藝，定能冠壓群雄！', '2');
