INSERT INTO `ol_stage` VALUES ('26', '1', '7', '2', '40721', '衙役', '0', '15', '216', '1017', '', '0', '', '大名府', '請光頭評委給我個機會！', '為什麼我這麼帥卻要掉頭發，你那麼醜卻不掉頭發？我給你機會，誰給我機會？', '', '', '2');
