INSERT INTO `ol_stage` VALUES ('52', '2', '5', '4', '41341', '蔡九', '0', '29', '1173', '6885', '地煞戰靴圖紙', '13304', '', '江州城', '蔡九，你不光私設收費站，還隨意提高加油站的油價！今天我就替你爹管教管教你！', '既然知道我爸爸是誰，那就趕緊掏錢。花錢買平安，划算。', '蔡京的兒子又怎麼樣，三兩下就搞定了。兄弟們，咱們去前面村莊買點豬肉，犒賞三軍！', '跟著大哥混，有肉吃！', '2');
INSERT INTO `ol_stage` VALUES ('53', '2', '6', '1', '41411', '李應', '0', '29', '421', '4065', '', '0', '', '祝家莊', '你這豬肉是翻著倍漲價的吧？這不搶錢麼？', '愛買不買，豬飼料先漲價，我不漲價就得虧本。', '', '', '2');
INSERT INTO `ol_stage` VALUES ('54', '2', '6', '2', '41421', '扈三娘', '1', '30', '421', '4321', '', '0', '', '祝家莊', '你這裡的豬飼料怎麼這麼貴？現在豬肉價格一漲，老百姓都吃不起肉了！', '我也沒辦法，飼料原材料漲價了，我不跟著漲就沒法開店了！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('55', '2', '6', '3', '41431', '祝家三虎', '0', '31', '421', '4437', '', '0', '', '祝家莊', '你為什麼要哄抬飼料原材料的價格？你知不知道這直接導致了豬肉價格上漲？', '不關我事啊，祝家莊的祝老莊主是這些原材料的唯一指定總經銷商，我們都是按他規定的價格進貨的。', '', '', '2');
