INSERT INTO `ol_stage` VALUES ('57', '2', '7', '1', '41511', '秀才', '0', '32', '450', '4820', '', '0', '', '大名府', '在下陽谷縣令，報名參加大名府鵲橋會！', '知府提督尚且在排隊，區區縣令算什麼？', '', '', '2');
