INSERT INTO `ol_stage` VALUES ('59', '2', '7', '3', '41531', '王太守', '1', '33', '450', '4780', '', '0', '', '大名府', '又來一個光頭……請再給我這大齡青年一個機會吧！', '小兄弟不要急，我們來討論一下你最喜歡的顏色先。', '', '', '2');
INSERT INTO `ol_stage` VALUES ('60', '2', '7', '4', '41541', '梁中書', '1', '33', '1350', '6895', '地煞甲胄圖紙', '12304', '', '大名府', '美女，我對你一見鍾情，讓我照顧你一輩子吧！', '你年薪有一億美金嗎？有獨棟別墅嗎？有私人飛機嗎？有私人遊輪嗎？', '現在的妹子太拜金了！罷了，我們化悲痛為力量，去汴梁城參加汴梁杯蹴鞠賽吧，說不定還能見到皇上！', '老大英明！贏了的話還能拿到一大筆獎金呢！', '2');
INSERT INTO `ol_stage` VALUES ('61', '2', '8', '1', '41611', '報名官', '0', '34', '480', '5322', '', '0', '', '汴梁城', '狹路相逢勇者勝，這第一場熱身賽，哥計畫完勝！', '那得問問我老人家，我能把有的球給吹沒了，也能把沒的球給吹有了。', '', '', '2');
