INSERT INTO `ol_stage` VALUES ('63', '2', '8', '3', '41631', '高衙內', '0', '35', '480', '5635', '', '0', '', '汴梁城', '久仰久仰，蹴鞠一霸說的就是您吧？', '好說，來，讓你見識一下我的鎖喉神功！', '', '', '2');
INSERT INTO `ol_stage` VALUES ('64', '2', '8', '4', '41641', '高俅', '0', '35', '1439', '7571', '地煞鋼刀圖紙', '14304', '', '汴梁城', '一路高歌猛進打入決賽，眼看冠軍在望，兄弟們拼了！', '井底之蛙大言不慚，老夫這首長秘書可是踢蹴鞠踢出來的，豈會輸給你們這幫黃口小兒乎？', '這奸賊高俅的確如此難搞，好在我沒有讓各位觀眾失望！兄弟們，咱們此舉也當載入史冊啊！', '太牛了，老大！高俅這奸臣竟然敗于咱們手上，咱們也可名垂青史，萬古流芳了！', '2');
