INSERT INTO `ol_stage` VALUES ('142', '5', '4', '2', '43621', '劉夫人', '0', '70', '1085', '37918', '', '0', '', '清風寨', '原來是傳說中的碰瓷啊，想做個好人也不容易。我是陽谷縣令，爾等不得放肆！', '少廢話，趕緊賠償醫藥費、精神損失費、青春損失費、服裝道具費，不賠別想走！', '', '', '0');
