INSERT INTO `ol_stage` VALUES ('145', '5', '5', '1', '43711', '士兵', '0', '70', '1116', '38401', '', '0', '', '江州城', '哎？這地方怎麼還有公路收費站呢？這又不是高速公路！', '少廢話！交錢走人！', '', '', '0');
