INSERT INTO `ol_stage` VALUES ('182', '6', '6', '2', '44621', '扈三娘', '1', '70', '1395', '64666', '', '0', '', '祝家莊', '你這裡的豬飼料怎麼這麼貴？現在豬肉價格一漲，老百姓都吃不起肉了！', '我也沒辦法，飼料原材料漲價了，我不跟著漲就沒法開店了！', '', '', '0');
INSERT INTO `ol_stage` VALUES ('183', '6', '6', '3', '44631', '祝家三虎', '0', '70', '1395', '65166', '', '0', '', '祝家莊', '你為什麼要哄抬飼料原材料的價格？你知不知道這直接導致了豬肉價格上漲？', '不關我事啊，祝家莊的祝老莊主是這些原材料的唯一指定總經銷商，我們都是按他規定的價格進貨的。', '', '', '0');
INSERT INTO `ol_stage` VALUES ('184', '6', '6', '4', '44641', '祝莊主', '0', '70', '4183', '141405', '【火】妖卡碎片', '20039', '', '祝家莊', '你身為飼料原材料的總經銷商，隨意調整價格，擾亂物價水準，你當我這縣令是擺設？', '我是唯一指定總經銷商，解釋權歸我所有。', '杯具啊，連豬肉漲價也要我出手，還是以前當山大王時逍遙自在，要是有個壓寨夫人該多好！', '老大，大名府最近在舉辦鵲橋相親會，憑你的人品武藝，定能冠壓群雄！', '0');
INSERT INTO `ol_stage` VALUES ('185', '6', '7', '1', '44711', '秀才', '0', '70', '1426', '71880', '', '0', '', '大名府', '在下陽谷縣令，報名參加大名府鵲橋會！', '知府提督尚且在排隊，區區縣令算什麼？', '', '', '0');
INSERT INTO `ol_stage` VALUES ('186', '6', '7', '2', '44721', '衙役', '0', '70', '1426', '64692', '', '0', '', '大名府', '請光頭評委給我個機會！', '為什麼我這麼帥卻要掉頭發，你那麼醜卻不掉頭發？我給你機會，誰給我機會？', '', '', '0');
INSERT INTO `ol_stage` VALUES ('187', '6', '7', '3', '44731', '王太守', '1', '70', '1426', '72336', '', '0', '', '大名府', '又來一個光頭……請再給我這大齡青年一個機會吧！', '小兄弟不要急，我們來討論一下你最喜歡的顏色先。', '', '', '0');
INSERT INTO `ol_stage` VALUES ('188', '6', '7', '4', '44741', '梁中書', '0', '70', '4277', '156963', '【山】妖卡碎片', '20040', '', '大名府', '美女，我對你一見鍾情，讓我照顧你一輩子吧！', '你年薪有一億美金嗎？有獨棟別墅嗎？有私人飛機嗎？有私人遊輪嗎？', '現在的妹子太拜金了！罷了，我們化悲痛為力量，去汴梁城參加汴梁杯蹴鞠賽吧，說不定還能見到皇上！', '老大英明！贏了的話還能拿到一大筆獎金呢！', '0');
INSERT INTO `ol_stage` VALUES ('189', '6', '8', '1', '44811', '報名官', '0', '70', '1457', '75096', '', '0', '', '汴梁城', '狹路相逢勇者勝，這第一場熱身賽，哥計畫完勝！', '那得問問我老人家，我能把有的球給吹沒了，也能把沒的球給吹有了。', '', '', '0');
INSERT INTO `ol_stage` VALUES ('190', '6', '8', '2', '44821', '陸謙', '0', '70', '1457', '75096', '', '0', '', '汴梁城', '你就是大帝？是森林大帝吧！', '天亮了，你看，我的護球像亨利吧？', '', '', '0');
INSERT INTO `ol_stage` VALUES ('191', '6', '8', '3', '44831', '高衙內', '0', '70', '1457', '80690', '', '0', '', '汴梁城', '久仰久仰，蹴鞠一霸說的就是您吧？', '好說，來，讓你見識一下我的鎖喉神功！', '', '', '0');
INSERT INTO `ol_stage` VALUES ('192', '6', '8', '4', '44841', '高俅', '0', '70', '4370', '128408', '【山】妖卡碎片', '20040', '', '汴梁城', '一路高歌猛進打入決賽，眼看冠軍在望，兄弟們拼了！', '井底之蛙大言不慚，老夫這首長秘書可是踢蹴鞠踢出來的，豈會輸給你們這幫黃口小兒乎？', '這奸賊高俅的確如此難搞，好在我沒有讓各位觀眾失望！兄弟們，咱們此舉也當載入史冊啊！', '太牛了，老大！高俅這奸臣竟然敗于咱們手上，咱們也可名垂青史，萬古流芳了！', '0');
