DROP TABLE IF EXISTS `ol_stage`;
CREATE TABLE `ol_stage` (
  `id` int(11) NOT NULL,
  `difficulty` tinyint(2) NOT NULL,
  `stage` tinyint(2) NOT NULL,
  `subStage` tinyint(2) NOT NULL,
  `npcid` int(11) NOT NULL,
  `name` varchar(200) NOT NULL,
  `sex` tinyint(1) NOT NULL DEFAULT '1',
  `stageLevel` tinyint(2) NOT NULL,
  `exp` int(11) NOT NULL,
  `maxHP` int(11) NOT NULL,
  `dlItem` varchar(100) NOT NULL DEFAULT '0' COMMENT '掉落',
  `dlItemID` int(11) NOT NULL,
  `iconId` varchar(100) NOT NULL DEFAULT '0',
  `stageName` varchar(100) NOT NULL,
  `dialog1` text NOT NULL COMMENT '對話內容一，對話中先說的內容',
  `dialog2` text NOT NULL COMMENT '對話內容二，對話中後說的內容',
  `dialog3` text NOT NULL COMMENT '美完擊殺時對話',
  `dialog4` text NOT NULL COMMENT '美完擊殺時對話',
  `dialogSort` tinyint(1) NOT NULL COMMENT '對話順序 \r\n1是NPC先說，玩家後說 \r\n2是玩家先說，NPC後說\r\n',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

